 into atmpid  from vk_rotinfo  where  vk_rotinfoid = avk_rotinfoid; 
 select vk_adstate_brief_f(atmpid, alang) into atmpbrief;
  set abrief= concat(abrief , '' , ifnull(atmpbrief,'') , ' ');
