  set abrief='';
select vkusr
 into atmpid  from vk_rotowner  where  vk_rotownerid = avk_rotownerid; 
 select vk_usr_brief_f(atmpid, alang) into atmpbrief;
  set abrief= concat(abrief , '' , ifnull(atmpbrief,'') , ' ');
else
  set abrief= '';
end if;
return abrief;
end  
