while not fetch_done do 
    if amref<>'' then
        set amref=concat(amref,',');
    end if;
    set amref=concat(amref,atmpbrief);
  set  fetch_done=false;
    fetch multiref_cursor into atmpbrief;
end while;
close multiref_cursor;
set amref=left(amref,255);
return amref;
end 
