create function vk_adstrg_brief_f  (
 avk_adstrgid binary(16)
 ,alang varchar(25)
) returns varchar(255) reads sql data  begin  
 declare abrief varchar(255);
 declare atmpstr varchar(255);
 declare atmpbrief varchar(255);
 declare atmpid binary(16);
 declare atmpmr varchar(255); 
 declare amlftemp varchar(255);
 declare amlfbrief varchar(255);
 declare aec int;
if avk_adstrgid is null then  set abrief=''; return abrief; end if;
select count(*) into aec from vk_adstrg where vk_adstrgid=avk_adstrgid;
if aec<>0 then
  set abrief='';
 select sex
 into aec  from vk_adstrg where vk_adstrgid=avk_adstrgid;
  case aec 
when 0 then 
  select concat(abrief ,
 'не существенно; ') into abrief ;
when 2 then 
  select concat(abrief ,
 'мужской; ') into abrief ;
when 1 then 
  select concat(abrief ,
 'женский; ') into abrief ;
 else 
  select concat(abrief ,
 '; ') into abrief ;
  end case; 
else
  set abrief= '';
end if;
return abrief;
end  
