create function vk_adstat_brief_f  (
 avk_adstatid binary(16)
 ,alang varchar(25)
) returns varchar(255) reads sql data  begin  
 declare abrief varchar(255);
 declare atmpstr varchar(255);
 declare atmpbrief varchar(255);
 declare atmpid binary(16);
 declare atmpmr varchar(255); 
 declare amlftemp varchar(255);
 declare amlfbrief varchar(255);
 declare aec int;
if avk_adstatid is null then  set abrief=''; return abrief; end if;
select count(*) into aec from vk_adstat where vk_adstatid=avk_adstatid;
if aec<>0 then
  set abrief='';
  select concat(abrief 
  , '' , ifnull(period,'') ,' '  )
  into abrief   from vk_adstat  where  vk_adstatid = avk_adstatid; 
  select concat(abrief 
  , '' , ifnull(s_day,'') ,' '  )
  into abrief   from vk_adstat  where  vk_adstatid = avk_adstatid; 
  select concat(abrief 
  , '' , ifnull(s_month,'') ,' '  )
  into abrief   from vk_adstat  where  vk_adstatid = avk_adstatid; 
  select concat(abrief 
  , '' , ifnull(overal,'') ,' '  )
  into abrief   from vk_adstat  where  vk_adstatid = avk_adstatid; 
