drop procedure if exists vk_position_delete
go

create procedure vk_position_delete /**/ (
 acursession varchar(38),
 avk_positionid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_position where vk_positionid=g2b(avk_positionid);
if aec>0 then
 select checkoperation( acursession ,'vdpos.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_position' result;
    leave body;
  end if;
 call vk_position_logger(acursession,avk_positionid) ; 
  delete from  vk_position 
  where  vk_positionid = g2b(avk_positionid) ;
  delete from num_values where ownerpartname='vk_position' and ownerrowid=g2b(avk_positionid);
  end if;
    select 'ok' result;
 end 
go
