drop procedure if exists vk_position_save
go

create procedure vk_position_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_positionid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* vkid *//* vkid */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_position where vk_positionid=g2b(avk_positionid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdpos.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_position' result;
    leave body;
  end if;
 start transaction ; 
 call vk_position_logger(acursession,avk_positionid) ; 
 update  vk_position set changestamp=now()
,
  title=atitle
,
  vkid=avkid
  where  vk_positionid = g2b(avk_positionid) ;


call vk_position_client_trigger(acursession,avk_positionid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdpos.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_position' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_position
 (  vk_positionid 
,instanceid
,title

,vkid

 ) values ( g2b(avk_positionid) 
,g2b(ainstanceid)
,atitle

,avkid

 ) ;


call vk_position_client_trigger(acursession,avk_positionid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


