create procedure vk_cab_delete /*рекламный кабинет*/ (
 acursession varchar(38),
 avk_cabid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_cab where vk_cabid=g2b(avk_cabid);
if aec>0 then
 select checkoperation( acursession ,'vkusr.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_cab' result;
    leave body;
  end if;
 call vk_cab_logger(acursession,avk_cabid) ; 
  delete from  vk_cab 
  where  vk_cabid = g2b(avk_cabid) ;
  delete from num_values where ownerpartname='vk_cab' and ownerrowid=g2b(avk_cabid);
  end if;
    select 'ok' result;
 end 
go
GO

/*рекламный кабинет*/

drop procedure if exists vk_cab_save
go

create procedure vk_cab_save /*рекламный кабинет*/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_cabid varchar(38)
,aname
 varchar (256)/* название *//* название */
,aaccount_id
 varchar (20)/* id *//* id */
,aaccount_type
 varchar (20)/* тип *//* тип */
,aaccount_status
 integer/* активен *//* активен */
,aaccess_role
 varchar (20)/* роль пользователя *//* роль пользователя */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_cab where vk_cabid=g2b(avk_cabid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkusr.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_cab' result;
    leave body;
  end if;
 start transaction ; 
 call vk_cab_logger(acursession,avk_cabid) ; 
 update  vk_cab set changestamp=now()
,
  name=aname
,
  account_id=aaccount_id
,
  account_type=aaccount_type
,
  account_status=aaccount_status
,
  access_role=aaccess_role
  where  vk_cabid = g2b(avk_cabid) ;


call vk_cab_client_trigger(acursession,avk_cabid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkusr.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_cab' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_cab
 (  vk_cabid 
,instanceid
,name

,account_id

,account_type

,account_status

,access_role

 ) values ( g2b(avk_cabid) 
,g2b(ainstanceid)
,aname

,aaccount_id

,aaccount_type

,aaccount_status

,aaccess_role

 ) ;


call vk_cab_client_trigger(acursession,avk_cabid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_cab_copy
go

create procedure vk_cab_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_cab 
 ( vk_cabid 
,instanceid
,name

,account_id

,account_type

,account_status

,access_role

 ) select 
copymap(acopyaction,b2g(vk_cabid)) 
,copymap(acopyaction,b2g(instanceid))
,name

,account_id

,account_type

,account_status

,access_role

 from vk_cab
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


drop procedure if exists vk_cab_logger
go

create procedure vk_cab_logger /*рекламный кабинет*/ (
 acursession varchar(38),
 avk_cabid varchar(38)
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
    leave body;
 end if;
 end 
go
GO


drop procedure if exists vk_cab_parent
go
create procedure vk_cab_parent /* рекламный кабинет*/ (
 acursession varchar(38),
 arowid binary(16) ,
out  aparentid binary(16),
out aparenttable varchar(255)
) body:begin  
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
  set aparenttable = 'instance';
  select instanceid into aparentid from vk_cab where  vk_cabid=arowid;
 end 
go
GO


drop procedure if exists vk_cab_islocked
go
create procedure vk_cab_islocked /* рекламный кабинет */ (
 acursession varchar(38),
 arowid binary(16) ,
 out aislocked integer
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare alockuserid binary(16);
 declare alocksessionid binary(16);
 declare aparenttable varchar(255); 
declare aec  int;
 set aislocked = 0;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select  usersid into auserid  from the_session where the_sessionid=acursession;
 select  lockuserid,locksessionid into alockuserid, alocksessionid from vk_cab where vk_cabid=arowid;
 /* verify this row */
 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists vk_cab_lock
go
create procedure vk_cab_lock /* рекламный кабинет */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call vk_cab_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update vk_cab set lockuserid =auserid ,locksessionid=null  where vk_cabid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_cab set lockuserid=null ,locksessionid =acursession  where vk_cabid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists vk_cab_unlock
go
create procedure vk_cab_unlock /* рекламный кабинет */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_cab_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_cab set lockuserid =null  where vk_cabid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_cab set locksessionid =null  where vk_cabid=arowid;
    leave body;
   end if;
 end 
go
GO


drop procedure IF EXISTS vk_cab_client_trigger
GO

create procedure vk_cab_client_trigger  (
 acursession varchar(38),
 aVK_CABid varchar(38)
) 
 begin  
   declare aBRIEF varchar(255);
  set aBRIEF='to do';
end 
GO


drop procedure if exists vk_usr_brief
go

create procedure vk_usr_brief  (
 acursession varchar(38),
 avk_usrid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_usrid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_usr where vk_usrid=g2b(avk_usrid);
 if  aec >0 then
   set abrief=vk_usr_brief_f(g2b(avk_usrid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_usr_delete
go

create procedure vk_usr_delete /*вк. пользователь*/ (
 acursession varchar(38),
 avk_usrid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_usr where vk_usrid=g2b(avk_usrid);
if aec>0 then
 select checkoperation( acursession ,'vkusr.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_usr' result;
    leave body;
  end if;
 call vk_usr_logger(acursession,avk_usrid) ; 
  delete from  vk_usr 
  where  vk_usrid = g2b(avk_usrid) ;
  delete from num_values where ownerpartname='vk_usr' and ownerrowid=g2b(avk_usrid);
  end if;
    select 'ok' result;
 end 
go
GO

/*вк. пользователь*/

drop procedure if exists vk_usr_save
