create procedure vk_usr_save /*вк. пользователь*/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_usrid varchar(38)
,avkid
 varchar (20)/* id *//* id */
,afirst_name
 varchar (255)/* имя *//* имя */
,alast_name
 varchar (256)/* фамилия *//* фамилия */
,adeactivated
 integer/* запись удалена *//* запись удалена */
,aphoto_id
 varchar (255)/* фото *//* фото */
,asex
 integer/* пол *//* пол */
,abdate
 varchar (20)/* дата рождения *//* дата рождения */
,acountry varchar(38)/* страна *//* страна */
,ahome_town varchar(38)/* родной город *//* родной город */
,ahas_photo
 integer/* есть фото *//* есть фото */
,aphoto_50 varchar(255)/* фото 50 *//* фото 50 */
,aphoto_100 varchar(255)/* фото 100 *//* фото 100 */
,aonline
 integer/* on-line *//* on-line */
,astatus
 varchar (255)/* статус *//* статус */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_usr where vk_usrid=g2b(avk_usrid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkusr.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_usr' result;
    leave body;
  end if;
 start transaction ; 
 call vk_usr_logger(acursession,avk_usrid) ; 
 update  vk_usr set changestamp=now()
,
  vkid=avkid
,
  first_name=afirst_name
,
  last_name=alast_name
,
  deactivated=adeactivated
,
  photo_id=aphoto_id
,
  sex=asex
,
  bdate=abdate
,
  country=g2b(acountry)
,
  home_town=g2b(ahome_town)
,
  has_photo=ahas_photo
,
  photo_50=aphoto_50
,
  photo_100=aphoto_100
,
  online=aonline
,
  status=astatus
  where  vk_usrid = g2b(avk_usrid) ;


call vk_usr_client_trigger(acursession,avk_usrid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkusr.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_usr' result;
    leave body;
  end if;
select count(*) into aec from vk_usr where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_usr>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_usr
 (  vk_usrid 
,instanceid
,vkid

,first_name

,last_name

,deactivated

,photo_id

,sex

,bdate

,country

,home_town

,has_photo

,photo_50

,photo_100

,online

,status

 ) values ( g2b(avk_usrid) 
,g2b(ainstanceid)
,avkid

,afirst_name

,alast_name

,adeactivated

,aphoto_id

,asex

,abdate

,g2b(acountry)

,g2b(ahome_town)

,ahas_photo

,aphoto_50

,aphoto_100

,aonline

,astatus

 ) ;


call vk_usr_client_trigger(acursession,avk_usrid);

 call vkusr_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


