/*описание*/

drop procedure if exists vk_rotinfo_save
go

create procedure vk_rotinfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_rotinfoid varchar(38)
,aname
 varchar (255)/* название *//* название */
,acampaign_id varchar(38)/* рекламная компания *//* рекламная компания */
,astatus varchar(38)/* статус *//* статус */
,a_description
 varchar (4096)/* описание *//* описание */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_rotinfo where vk_rotinfoid=g2b(avk_rotinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkrot.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_rotinfo' result;
    leave body;
  end if;
 start transaction ; 
 call vk_rotinfo_logger(acursession,avk_rotinfoid) ; 
 update  vk_rotinfo set changestamp=now()
,
  name=aname
,
  campaign_id=g2b(acampaign_id)
,
  status=g2b(astatus)
,
  _description=a_description
  where  vk_rotinfoid = g2b(avk_rotinfoid) ;


call vk_rotinfo_client_trigger(acursession,avk_rotinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkrot.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_rotinfo' result;
    leave body;
  end if;
select count(*) into aec from vk_rotinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_rotinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_rotinfo
 (  vk_rotinfoid 
,instanceid
,name

,campaign_id

,status

,_description

 ) values ( g2b(avk_rotinfoid) 
,g2b(ainstanceid)
,aname

,g2b(acampaign_id)

,g2b(astatus)

,a_description

 ) ;


call vk_rotinfo_client_trigger(acursession,avk_rotinfoid);

 call vkrot_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_rotinfo_copy
go

create procedure vk_rotinfo_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_rotinfo 
 ( vk_rotinfoid 
,instanceid
,name

,campaign_id

,status

,_description

 ) select 
copymap(acopyaction,b2g(vk_rotinfoid)) 
,copymap(acopyaction,b2g(instanceid))
,name

,campaign_id

,status

,_description

 from vk_rotinfo
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


drop procedure if exists vk_rotinfo_logger
go

create procedure vk_rotinfo_logger /**/ (
 acursession varchar(38),
 avk_rotinfoid varchar(38)
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
    leave body;
 end if;
 end 
go
GO


drop procedure if exists vk_rotinfo_parent
go
create procedure vk_rotinfo_parent /* */ (
 acursession varchar(38),
 arowid binary(16) ,
out  aparentid binary(16),
out aparenttable varchar(255)
) body:begin  
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
  set aparenttable = 'instance';
  select instanceid into aparentid from vk_rotinfo where  vk_rotinfoid=arowid;
 end 
go
GO


drop procedure if exists vk_rotinfo_islocked
go
create procedure vk_rotinfo_islocked /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 out aislocked integer
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare alockuserid binary(16);
 declare alocksessionid binary(16);
 declare aparenttable varchar(255); 
declare aec  int;
 set aislocked = 0;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select  usersid into auserid  from the_session where the_sessionid=acursession;
 select  lockuserid,locksessionid into alockuserid, alocksessionid from vk_rotinfo where vk_rotinfoid=arowid;
 /* verify this row */
 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists vk_rotinfo_lock
go
create procedure vk_rotinfo_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call vk_rotinfo_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update vk_rotinfo set lockuserid =auserid ,locksessionid=null  where vk_rotinfoid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_rotinfo set lockuserid=null ,locksessionid =acursession  where vk_rotinfoid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists vk_rotinfo_unlock
go
create procedure vk_rotinfo_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_rotinfo_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_rotinfo set lockuserid =null  where vk_rotinfoid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_rotinfo set locksessionid =null  where vk_rotinfoid=arowid;
    leave body;
   end if;
 end 
go
GO


