drop procedure if exists vk_age_brief
go

create procedure vk_age_brief  (
 acursession varchar(38),
 avk_ageid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_ageid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_age where vk_ageid=g2b(avk_ageid);
 if  aec >0 then
   set abrief=vk_age_brief_f(g2b(avk_ageid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_age_delete
go

create procedure vk_age_delete /**/ (
 acursession varchar(38),
 avk_ageid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_age where vk_ageid=g2b(avk_ageid);
if aec>0 then
 select checkoperation( acursession ,'vdage.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_age' result;
    leave body;
  end if;
 call vk_age_logger(acursession,avk_ageid) ; 
  delete from  vk_age 
  where  vk_ageid = g2b(avk_ageid) ;
  delete from num_values where ownerpartname='vk_age' and ownerrowid=g2b(avk_ageid);
  end if;
    select 'ok' result;
 end 
go
GO

/*ограничения*/

drop procedure if exists vk_age_save
go

create procedure vk_age_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_ageid varchar(38)
,avkid
 integer/* vkid *//* vkid */
,atitle
 varchar (255)/* название *//* название */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_age where vk_ageid=g2b(avk_ageid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdage.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_age' result;
    leave body;
  end if;
 start transaction ; 
 call vk_age_logger(acursession,avk_ageid) ; 
 update  vk_age set changestamp=now()
,
  vkid=avkid
,
  title=atitle
  where  vk_ageid = g2b(avk_ageid) ;


call vk_age_client_trigger(acursession,avk_ageid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdage.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_age' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_age
 (  vk_ageid 
,instanceid
,vkid

,title

 ) values ( g2b(avk_ageid) 
,g2b(ainstanceid)
,avkid

,atitle

 ) ;


call vk_age_client_trigger(acursession,avk_ageid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_age_copy
go

create procedure vk_age_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_age 
 ( vk_ageid 
,instanceid
,vkid

,title

 ) select 
copymap(acopyaction,b2g(vk_ageid)) 
,copymap(acopyaction,b2g(instanceid))
,vkid

,title

 from vk_age
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


