    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_prjowner where vk_prjownerid=g2b(avk_prjownerid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkprj.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_prjowner' result;
    leave body;
  end if;
 start transaction ; 
 call vk_prjowner_logger(acursession,avk_prjownerid) ; 
 update  vk_prjowner set changestamp=now()
,
  vkusr=g2b(avkusr)
  where  vk_prjownerid = g2b(avk_prjownerid) ;


call vk_prjowner_client_trigger(acursession,avk_prjownerid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkprj.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_prjowner' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_prjowner
 (  vk_prjownerid 
,instanceid
,vkusr

 ) values ( g2b(avk_prjownerid) 
,g2b(ainstanceid)
,g2b(avkusr)

 ) ;


call vk_prjowner_client_trigger(acursession,avk_prjownerid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


