drop procedure if exists vk_region_brief
go

create procedure vk_region_brief  (
 acursession varchar(38),
 avk_regionid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_regionid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_region where vk_regionid=g2b(avk_regionid);
 if  aec >0 then
   set abrief=vk_region_brief_f(g2b(avk_regionid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_region_delete
go

create procedure vk_region_delete /**/ (
 acursession varchar(38),
 avk_regionid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_region where vk_regionid=g2b(avk_regionid);
if aec>0 then
 select checkoperation( acursession ,'vdrg.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_region' result;
    leave body;
  end if;
 call vk_region_logger(acursession,avk_regionid) ; 
  delete from  vk_region 
  where  vk_regionid = g2b(avk_regionid) ;
  delete from num_values where ownerpartname='vk_region' and ownerrowid=g2b(avk_regionid);
  end if;
    select 'ok' result;
 end 
go
GO

