drop procedure if exists vk_region_save
go

create procedure vk_region_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_regionid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* id *//* id */
,acountry_id varchar(38)/* страна *//* страна */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_region where vk_regionid=g2b(avk_regionid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdrg.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_region' result;
    leave body;
  end if;
 start transaction ; 
 call vk_region_logger(acursession,avk_regionid) ; 
 update  vk_region set changestamp=now()
,
  title=atitle
,
  vkid=avkid
,
  country_id=g2b(acountry_id)
  where  vk_regionid = g2b(avk_regionid) ;


call vk_region_client_trigger(acursession,avk_regionid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdrg.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_region' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_region
 (  vk_regionid 
,instanceid
,title

,vkid

,country_id

 ) values ( g2b(avk_regionid) 
,g2b(ainstanceid)
,atitle

,avkid

,g2b(acountry_id)

 ) ;


call vk_region_client_trigger(acursession,avk_regionid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


