drop procedure if exists vk_tasktype_brief
go

create procedure vk_tasktype_brief  (
 acursession varchar(38),
 avk_tasktypeid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_tasktypeid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_tasktype where vk_tasktypeid=g2b(avk_tasktypeid);
 if  aec >0 then
   set abrief=vk_tasktype_brief_f(g2b(avk_tasktypeid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_tasktype_delete
go

create procedure vk_tasktype_delete /**/ (
 acursession varchar(38),
 avk_tasktypeid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_tasktype where vk_tasktypeid=g2b(avk_tasktypeid);
if aec>0 then
 select checkoperation( acursession ,'vdtt.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_tasktype' result;
    leave body;
  end if;
 call vk_tasktype_logger(acursession,avk_tasktypeid) ; 
  delete from  vk_tasktype 
  where  vk_tasktypeid = g2b(avk_tasktypeid) ;
  delete from num_values where ownerpartname='vk_tasktype' and ownerrowid=g2b(avk_tasktypeid);
  end if;
    select 'ok' result;
 end 
go
GO

/*тип задачи*/

drop procedure if exists vk_tasktype_save
go

create procedure vk_tasktype_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_tasktypeid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,arepeatabletask
 integer/* периодическая задача *//* периодическая задача */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_tasktype where vk_tasktypeid=g2b(avk_tasktypeid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdtt.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_tasktype' result;
    leave body;
  end if;
 start transaction ; 
 call vk_tasktype_logger(acursession,avk_tasktypeid) ; 
 update  vk_tasktype set changestamp=now()
,
  title=atitle
,
  repeatabletask=arepeatabletask
  where  vk_tasktypeid = g2b(avk_tasktypeid) ;


call vk_tasktype_client_trigger(acursession,avk_tasktypeid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdtt.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_tasktype' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_tasktype
 (  vk_tasktypeid 
,instanceid
,title

,repeatabletask

 ) values ( g2b(avk_tasktypeid) 
,g2b(ainstanceid)
,atitle

,arepeatabletask

 ) ;


call vk_tasktype_client_trigger(acursession,avk_tasktypeid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_tasktype_copy
go

create procedure vk_tasktype_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_tasktype 
 ( vk_tasktypeid 
,instanceid
,title

,repeatabletask

 ) select 
copymap(acopyaction,b2g(vk_tasktypeid)) 
,copymap(acopyaction,b2g(instanceid))
,title

,repeatabletask

 from vk_tasktype
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


