    leave body;
   end if;
   if  aislocked =2  then
    update workplace set lockuserid =null  where workplaceid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update workplace set locksessionid =null  where workplaceid=arowid;
    leave body;
   end if;
 end 
go
GO


