drop procedure if exists vk_country_brief
go

create procedure vk_country_brief  (
 acursession varchar(38),
 avk_countryid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_countryid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_country where vk_countryid=g2b(avk_countryid);
 if  aec >0 then
   set abrief=vk_country_brief_f(g2b(avk_countryid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_country_delete
go

create procedure vk_country_delete /**/ (
 acursession varchar(38),
 avk_countryid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_country where vk_countryid=g2b(avk_countryid);
if aec>0 then
 select checkoperation( acursession ,'vdcn.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_country' result;
    leave body;
  end if;
 call vk_country_logger(acursession,avk_countryid) ; 
  delete from  vk_country 
  where  vk_countryid = g2b(avk_countryid) ;
  delete from num_values where ownerpartname='vk_country' and ownerrowid=g2b(avk_countryid);
  end if;
    select 'ok' result;
 end 
go
GO

/*страна*/

drop procedure if exists vk_country_save
go

create procedure vk_country_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_countryid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* id *//* id */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_country where vk_countryid=g2b(avk_countryid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdcn.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_country' result;
    leave body;
  end if;
 start transaction ; 
 call vk_country_logger(acursession,avk_countryid) ; 
 update  vk_country set changestamp=now()
,
  title=atitle
,
  vkid=avkid
  where  vk_countryid = g2b(avk_countryid) ;


call vk_country_client_trigger(acursession,avk_countryid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdcn.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_country' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_country
 (  vk_countryid 
,instanceid
,title

,vkid

 ) values ( g2b(avk_countryid) 
,g2b(ainstanceid)
,atitle

,avkid

 ) ;


call vk_country_client_trigger(acursession,avk_countryid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_country_copy
go

create procedure vk_country_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_country 
 ( vk_countryid 
,instanceid
,title

,vkid

 ) select 
copymap(acopyaction,b2g(vk_countryid)) 
,copymap(acopyaction,b2g(instanceid))
,title

,vkid

 from vk_country
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


