 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_townid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_town where vk_townid=g2b(avk_townid);
 if  aec >0 then
   set abrief=vk_town_brief_f(g2b(avk_townid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_town_delete
go

create procedure vk_town_delete /**/ (
 acursession varchar(38),
 avk_townid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_town where vk_townid=g2b(avk_townid);
if aec>0 then
 select checkoperation( acursession ,'vdt.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_town' result;
    leave body;
  end if;
 call vk_town_logger(acursession,avk_townid) ; 
  delete from  vk_town 
  where  vk_townid = g2b(avk_townid) ;
  delete from num_values where ownerpartname='vk_town' and ownerrowid=g2b(avk_townid);
  end if;
    select 'ok' result;
 end 
go
GO

/*город*/

drop procedure if exists vk_town_save
go

create procedure vk_town_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_townid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* id *//* id */
,acountry_id varchar(38)/* страна *//* страна */
,aregion_id varchar(38)/* регион *//* регион */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_town where vk_townid=g2b(avk_townid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdt.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_town' result;
    leave body;
  end if;
 start transaction ; 
 call vk_town_logger(acursession,avk_townid) ; 
 update  vk_town set changestamp=now()
,
  title=atitle
,
  vkid=avkid
,
  country_id=g2b(acountry_id)
,
  region_id=g2b(aregion_id)
  where  vk_townid = g2b(avk_townid) ;


call vk_town_client_trigger(acursession,avk_townid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdt.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_town' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_town
 (  vk_townid 
,instanceid
,title

,vkid

,country_id

,region_id

 ) values ( g2b(avk_townid) 
,g2b(ainstanceid)
,atitle

,avkid

,g2b(acountry_id)

,g2b(aregion_id)

 ) ;


call vk_town_client_trigger(acursession,avk_townid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_town_copy
go

create procedure vk_town_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_town 
 ( vk_townid 
,instanceid
,title

,vkid

,country_id

,region_id

 ) select 
copymap(acopyaction,b2g(vk_townid)) 
,copymap(acopyaction,b2g(instanceid))
,title

,vkid

,country_id

,region_id

 from vk_town
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


