drop procedure if exists vk_adsdaydemografy_brief
go

create procedure vk_adsdaydemografy_brief  (
 acursession varchar(38),
 avk_adsdaydemografyid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_adsdaydemografyid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_adsdaydemografy where vk_adsdaydemografyid=g2b(avk_adsdaydemografyid);
 if  aec >0 then
   set abrief=vk_adsdaydemografy_brief_f(g2b(avk_adsdaydemografyid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_adsdaydemografy_delete
go

create procedure vk_adsdaydemografy_delete /**/ (
 acursession varchar(38),
 avk_adsdaydemografyid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adsdaydemografy where vk_adsdaydemografyid=g2b(avk_adsdaydemografyid);
if aec>0 then
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_adsdaydemografy' result;
    leave body;
  end if;
 call vk_adsdaydemografy_logger(acursession,avk_adsdaydemografyid) ; 
  delete from  vk_adsdaydemografy 
  where  vk_adsdaydemografyid = g2b(avk_adsdaydemografyid) ;
  delete from num_values where ownerpartname='vk_adsdaydemografy' and ownerrowid=g2b(avk_adsdaydemografyid);
  end if;
    select 'ok' result;
 end 
go
GO

