drop procedure if exists vk_adsdaydemografy_save
go

create procedure vk_adsdaydemografy_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_adsdaydemografyid varchar(38)
,as_day
 varchar (10)/* день *//* день */
,arecordtype
 varchar (40)/* тип записи *//* тип записи */
,aimpression_rate
 double/* просмотры *//* просмотры */
,aclick_rate
 double/* клики *//* клики */
,a_value
 varchar (64)/* значение *//* значение */
,aname
 varchar (255)/* название *//* название */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adsdaydemografy where vk_adsdaydemografyid=g2b(avk_adsdaydemografyid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_adsdaydemografy' result;
    leave body;
  end if;
 start transaction ; 
 call vk_adsdaydemografy_logger(acursession,avk_adsdaydemografyid) ; 
 update  vk_adsdaydemografy set changestamp=now()
,
  s_day=as_day
,
  recordtype=arecordtype
,
  impression_rate=aimpression_rate
,
  click_rate=aclick_rate
,
  _value=a_value
,
  name=aname
  where  vk_adsdaydemografyid = g2b(avk_adsdaydemografyid) ;


call vk_adsdaydemografy_client_trigger(acursession,avk_adsdaydemografyid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_adsdaydemografy' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_adsdaydemografy
 (  vk_adsdaydemografyid 
,instanceid
,s_day

,recordtype

,impression_rate

,click_rate

,_value

,name

 ) values ( g2b(avk_adsdaydemografyid) 
,g2b(ainstanceid)
,as_day

,arecordtype

,aimpression_rate

,aclick_rate

,a_value

,aname

 ) ;


call vk_adsdaydemografy_client_trigger(acursession,avk_adsdaydemografyid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


