create procedure vk_adinfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_adinfoid varchar(38)
,aname
 varchar (255)/* название *//* название */
,aads_id
 varchar (30)/* id *//* id */
,acampaign_id varchar(38)/* кампания *//* кампания */
