,aage_restriction varchar(38)/* ограничение *//* ограничение */
,acost_type varchar(38)/* тип оплаты *//* тип оплаты */
,acpc
 integer/* за переход(коп) *//* за переход(коп) */
,acpm
 integer/* за 1000 показов(коп) *//* за 1000 показов(коп) */
,aad_platform varchar(38)/* площадки *//* площадки */
,acategory1_id varchar(38)/* категория 1 *//* категория 1 */
,acategory2_id varchar(38)/* категория 2 *//* категория 2 */
,astatus varchar(38)/* статус *//* статус */
,aimpression_limit
 integer/* ограничение показов *//* ограничение показов */
,aall_limit
 double/* лимит (руб) *//* лимит (руб) */
,aapproved varchar(38)/* модерация *//* модерация */
,avideo
 integer/* реклама видео *//* реклама видео */
,adisclamer
 integer/* предупреждения *//* предупреждения */
,atitle
 varchar (255)/* заголовок *//* заголовок */
,a_description
 varchar (4096)/* описание *//* описание */
,alink_url varchar(255)/* рекламируем *//* рекламируем */
,alink_domain
 varchar (120)/* домен рекламы *//* домен рекламы */
,apreview_link varchar(255)/* превью *//* превью */
,aimage_src varchar(255)/* изображение *//* изображение */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adinfo where vk_adinfoid=g2b(avk_adinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_adinfo' result;
    leave body;
  end if;
 start transaction ; 
 call vk_adinfo_logger(acursession,avk_adinfoid) ; 
 update  vk_adinfo set changestamp=now()
,
  name=aname
,
  ads_id=aads_id
,
  campaign_id=g2b(acampaign_id)
,
  ad_format=g2b(aad_format)
,
  age_restriction=g2b(aage_restriction)
,
  cost_type=g2b(acost_type)
,
  cpc=acpc
,
  cpm=acpm
,
  ad_platform=g2b(aad_platform)
,
  category1_id=g2b(acategory1_id)
,
  category2_id=g2b(acategory2_id)
,
  status=g2b(astatus)
,
  impression_limit=aimpression_limit
,
  all_limit=aall_limit
,
  approved=g2b(aapproved)
,
  video=avideo
,
  disclamer=adisclamer
,
  title=atitle
,
  _description=a_description
,
  link_url=alink_url
,
  link_domain=alink_domain
,
  preview_link=apreview_link
,
  image_src=aimage_src
  where  vk_adinfoid = g2b(avk_adinfoid) ;


call vk_adinfo_client_trigger(acursession,avk_adinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_adinfo' result;
    leave body;
  end if;
select count(*) into aec from vk_adinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_adinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_adinfo
 (  vk_adinfoid 
,instanceid
,name

,ads_id

,campaign_id

,ad_format

,age_restriction

,cost_type

,cpc

,cpm

,ad_platform

,category1_id

,category2_id

,status

,impression_limit

,all_limit

,approved

,video

,disclamer

,title

,_description

,link_url

,link_domain

,preview_link

,image_src

 ) values ( g2b(avk_adinfoid) 
,g2b(ainstanceid)
,aname

,aads_id

,g2b(acampaign_id)

,g2b(aad_format)

,g2b(aage_restriction)

,g2b(acost_type)

,acpc

,acpm

,g2b(aad_platform)

,g2b(acategory1_id)

,g2b(acategory2_id)

,g2b(astatus)

,aimpression_limit

,aall_limit

,g2b(aapproved)

,avideo

,adisclamer

,atitle

,a_description

,alink_url

,alink_domain

,apreview_link

,aimage_src

 ) ;


call vk_adinfo_client_trigger(acursession,avk_adinfoid);

 call vkads_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
