,image_src

 ) select 
copymap(acopyaction,b2g(vk_adinfoid)) 
,copymap(acopyaction,b2g(instanceid))
,name

,ads_id

,campaign_id

,ad_format

,age_restriction

,cost_type

,cpc

,cpm

,ad_platform

,category1_id

,category2_id

,status

,impression_limit

,all_limit

,approved

,video

,disclamer

,title

,_description

,link_url

,link_domain

,preview_link

,image_src

 from vk_adinfo
 where instanceid =g2b( ainstanceid); 
 end 
go
