drop procedure if exists vk_adsowner_brief
go

create procedure vk_adsowner_brief  (
 acursession varchar(38),
 avk_adsownerid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_adsownerid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_adsowner where vk_adsownerid=g2b(avk_adsownerid);
 if  aec >0 then
   set abrief=vk_adsowner_brief_f(g2b(avk_adsownerid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_adsowner_delete
go

create procedure vk_adsowner_delete /**/ (
 acursession varchar(38),
 avk_adsownerid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adsowner where vk_adsownerid=g2b(avk_adsownerid);
if aec>0 then
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_adsowner' result;
    leave body;
  end if;
 call vk_adsowner_logger(acursession,avk_adsownerid) ; 
  delete from  vk_adsowner 
  where  vk_adsownerid = g2b(avk_adsownerid) ;
  delete from num_values where ownerpartname='vk_adsowner' and ownerrowid=g2b(avk_adsownerid);
  end if;
    select 'ok' result;
 end 
go
GO

/*владелец*/

drop procedure if exists vk_adsowner_save
go

create procedure vk_adsowner_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_adsownerid varchar(38)
,avkusr varchar(38)/* владелец *//* владелец */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adsowner where vk_adsownerid=g2b(avk_adsownerid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_adsowner' result;
    leave body;
  end if;
 start transaction ; 
 call vk_adsowner_logger(acursession,avk_adsownerid) ; 
 update  vk_adsowner set changestamp=now()
,
  vkusr=g2b(avkusr)
  where  vk_adsownerid = g2b(avk_adsownerid) ;


call vk_adsowner_client_trigger(acursession,avk_adsownerid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_adsowner' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_adsowner
 (  vk_adsownerid 
,instanceid
,vkusr

 ) values ( g2b(avk_adsownerid) 
,g2b(ainstanceid)
,g2b(avkusr)

 ) ;


call vk_adsowner_client_trigger(acursession,avk_adsownerid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


