drop procedure if exists vk_adovstat_save
go

create procedure vk_adovstat_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_adovstatid varchar(38)
,aspent
 double/* потрачено *//* потрачено */
,ajoin_rate
 integer/* подписки *//* подписки */
,aimpressions
 integer/* просмотры *//* просмотры */
,aclicks
 integer/* клики *//* клики */
,areach
 integer/* охват *//* охват */
,avideo_views
 integer/* просмотры *//* просмотры */
,avideo_views_half
 integer/* просмотры половины *//* просмотры половины */
,avideo_views_full
 integer/* просмотры целого *//* просмотры целого */
,avideo_clicks_site
 integer/* переходы на сайт *//* переходы на сайт */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adovstat where vk_adovstatid=g2b(avk_adovstatid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_adovstat' result;
    leave body;
  end if;
 start transaction ; 
 call vk_adovstat_logger(acursession,avk_adovstatid) ; 
 update  vk_adovstat set changestamp=now()
,
  spent=aspent
,
  join_rate=ajoin_rate
,
  impressions=aimpressions
,
  clicks=aclicks
,
  reach=areach
,
  video_views=avideo_views
,
  video_views_half=avideo_views_half
,
  video_views_full=avideo_views_full
,
  video_clicks_site=avideo_clicks_site
  where  vk_adovstatid = g2b(avk_adovstatid) ;


call vk_adovstat_client_trigger(acursession,avk_adovstatid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_adovstat' result;
    leave body;
  end if;
select count(*) into aec from vk_adovstat where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_adovstat>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_adovstat
 (  vk_adovstatid 
,instanceid
,spent

,join_rate

,impressions

,clicks

,reach

,video_views

,video_views_half

,video_views_full

,video_clicks_site

 ) values ( g2b(avk_adovstatid) 
,g2b(ainstanceid)
,aspent

,ajoin_rate

,aimpressions

,aclicks

,areach

,avideo_views

,avideo_views_half

,avideo_views_full

,avideo_clicks_site

 ) ;


call vk_adovstat_client_trigger(acursession,avk_adovstatid);

 call vkads_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


