 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists vk_adovstat_lock
go
create procedure vk_adovstat_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call vk_adovstat_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update vk_adovstat set lockuserid =auserid ,locksessionid=null  where vk_adovstatid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_adovstat set lockuserid=null ,locksessionid =acursession  where vk_adovstatid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists vk_adovstat_unlock
go
create procedure vk_adovstat_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_adovstat_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_adovstat set lockuserid =null  where vk_adovstatid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_adovstat set locksessionid =null  where vk_adovstatid=arowid;
    leave body;
   end if;
 end 
go
GO


