 integer/* использует голоса  вк *//* использует голоса  вк */
,aage_from
 integer/* возраст от *//* возраст от */
,aage_to
 integer/* возраст до *//* возраст до */
,aschool_from
 integer/* окончание школы с *//* окончание школы с */
,aschool_to
 integer/* окончание школыпо *//* окончание школыпо */
,auni_from
 integer/* окончание вуза *//* окончание вуза */
,auni_to
 integer/* окончание вуза *//* окончание вуза */
,acountry
 varchar (400)/* страна *//* страна */
,acities
 varchar (400)/* города *//* города */
,acities_not
 varchar (400)/* исключая города *//* исключая города */
