 varchar (255)/* группы *//* группы */
,agroups_not
 varchar (255)/* исключая группы *//* исключая группы */
,aapps
 varchar (255)/* приложения *//* приложения */
,aapps_not
 varchar (255)/* исключая приложения *//* исключая приложения */
,auser_devices
 varchar (255)/* устройства *//* устройства */
,auser_os
 varchar (255)/* оп. системы *//* оп. системы */
,auser_browsers
 varchar (255)/* браузеры *//* браузеры */
,aretargeting_groups
 varchar (255)/* группы ретаргетинга *//* группы ретаргетинга */
,aretargeting_groups_not
 varchar (255)/* исключая группы *//* исключая группы */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adstrg where vk_adstrgid=g2b(avk_adstrgid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_adstrg' result;
    leave body;
  end if;
 start transaction ; 
 call vk_adstrg_logger(acursession,avk_adstrgid) ; 
 update  vk_adstrg set changestamp=now()
,
  sex=asex
,
