  where  vk_adstrgid = g2b(avk_adstrgid) ;


call vk_adstrg_client_trigger(acursession,avk_adstrgid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_adstrg' result;
    leave body;
  end if;
select count(*) into aec from vk_adstrg where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_adstrg>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_adstrg
 (  vk_adstrgid 
,instanceid
,sex

,birthday

,statuses

,positions

,paying

,age_from

,age_to

,school_from

,school_to

,uni_from

,uni_to

,country

,cities

,cities_not

,districts

,stations

,streets

,religions

,interests

,interest_categories

,travellers

,groups

,groups_not

,apps

,apps_not

,user_devices

,user_os

,user_browsers

,retargeting_groups

,retargeting_groups_not

 ) values ( g2b(avk_adstrgid) 
,g2b(ainstanceid)
,asex

,abirthday

,astatuses

,apositions

,apaying

,aage_from

,aage_to

,aschool_from

,aschool_to

,auni_from

,auni_to

