,aapps

,aapps_not

,auser_devices

,auser_os

,auser_browsers

,aretargeting_groups

,aretargeting_groups_not

 ) ;


call vk_adstrg_client_trigger(acursession,avk_adstrgid);

 call vkads_initownership( acursession,ainstanceid);
 end if;
 commit; 
