,recordtype

,impression_rate

,click_rate

,_value

,name

 ) values ( g2b(avk_adsovdemografyid) 
,g2b(ainstanceid)
,arecordtype

,aimpression_rate

,aclick_rate

,a_value

,aname

 ) ;


call vk_adsovdemografy_client_trigger(acursession,avk_adsovdemografyid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


