drop procedure if exists vk_adsovdemografy_parent
go
create procedure vk_adsovdemografy_parent /* */ (
 acursession varchar(38),
 arowid binary(16) ,
out  aparentid binary(16),
out aparenttable varchar(255)
) body:begin  
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
  set aparenttable = 'instance';
  select instanceid into aparentid from vk_adsovdemografy where  vk_adsovdemografyid=arowid;
 end 
go
GO


drop procedure if exists vk_adsovdemografy_islocked
go
create procedure vk_adsovdemografy_islocked /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 out aislocked integer
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare alockuserid binary(16);
 declare alocksessionid binary(16);
 declare aparenttable varchar(255); 
declare aec  int;
 set aislocked = 0;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select  usersid into auserid  from the_session where the_sessionid=acursession;
 select  lockuserid,locksessionid into alockuserid, alocksessionid from vk_adsovdemografy where vk_adsovdemografyid=arowid;
 /* verify this row */
 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists vk_adsovdemografy_lock
go
create procedure vk_adsovdemografy_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call vk_adsovdemografy_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update vk_adsovdemografy set lockuserid =auserid ,locksessionid=null  where vk_adsovdemografyid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_adsovdemografy set lockuserid=null ,locksessionid =acursession  where vk_adsovdemografyid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists vk_adsovdemografy_unlock
go
create procedure vk_adsovdemografy_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_adsovdemografy_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_adsovdemografy set lockuserid =null  where vk_adsovdemografyid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_adsovdemografy set locksessionid =null  where vk_adsovdemografyid=arowid;
    leave body;
   end if;
 end 
go
GO


