drop procedure if exists vk_adsutm_brief
go

create procedure vk_adsutm_brief  (
 acursession varchar(38),
 avk_adsutmid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_adsutmid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_adsutm where vk_adsutmid=g2b(avk_adsutmid);
 if  aec >0 then
   set abrief=vk_adsutm_brief_f(g2b(avk_adsutmid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_adsutm_delete
go

create procedure vk_adsutm_delete /**/ (
 acursession varchar(38),
 avk_adsutmid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adsutm where vk_adsutmid=g2b(avk_adsutmid);
if aec>0 then
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_adsutm' result;
    leave body;
  end if;
 call vk_adsutm_logger(acursession,avk_adsutmid) ; 
  delete from  vk_adsutm 
  where  vk_adsutmid = g2b(avk_adsutmid) ;
  delete from num_values where ownerpartname='vk_adsutm' and ownerrowid=g2b(avk_adsutmid);
  end if;
    select 'ok' result;
 end 
go
GO

/*utm разметка*/

drop procedure if exists vk_adsutm_save
go

create procedure vk_adsutm_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_adsutmid varchar(38)
,aautoutm
 integer/* стандартное *//* стандартное */
,autm_source
 varchar (64)/* источник *//* источник */
,autm_medium
 varchar (64)/* канал *//* канал */
,autm_term
 varchar (64)/* ключевое слово  *//* ключевое слово  */
,autm_content
 varchar (64)/* содержание  *//* содержание  */
,autm_campaign
 varchar (64)/* название *//* название */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_adsutm where vk_adsutmid=g2b(avk_adsutmid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_adsutm' result;
    leave body;
  end if;
 start transaction ; 
 call vk_adsutm_logger(acursession,avk_adsutmid) ; 
 update  vk_adsutm set changestamp=now()
,
  autoutm=aautoutm
,
  utm_source=autm_source
,
  utm_medium=autm_medium
,
  utm_term=autm_term
,
  utm_content=autm_content
,
  utm_campaign=autm_campaign
  where  vk_adsutmid = g2b(avk_adsutmid) ;


call vk_adsutm_client_trigger(acursession,avk_adsutmid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkads.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_adsutm' result;
    leave body;
  end if;
select count(*) into aec from vk_adsutm where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_adsutm>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_adsutm
 (  vk_adsutmid 
,instanceid
,autoutm

,utm_source

,utm_medium

,utm_term

,utm_content

,utm_campaign

 ) values ( g2b(avk_adsutmid) 
,g2b(ainstanceid)
,aautoutm

,autm_source

,autm_medium

,autm_term

,autm_content

,autm_campaign

 ) ;


call vk_adsutm_client_trigger(acursession,avk_adsutmid);

 call vkads_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


