drop procedure if exists vk_adstat_copy
go

create procedure vk_adstat_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_adstat 
 ( vk_adstatid 
,instanceid
,period

,s_day

,s_month

,overal

,spent

,join_rate

,impressions

,clicks

,reach

,video_views

,video_views_half

,video_views_full

,video_clicks_site

 ) select 
copymap(acopyaction,b2g(vk_adstatid)) 
,copymap(acopyaction,b2g(instanceid))
,period

,s_day

,s_month

,overal

,spent

,join_rate

,impressions

,clicks

,reach

,video_views

,video_views_half

,video_views_full

,video_clicks_site

 from vk_adstat
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


