 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into iu_rcfg_mod 
 ( iu_rcfg_modid 
,instanceid
,sequence

,caption

,moduleaccessible

,theicon

,name

,groupname

,allobjects

,colegsobject

,substructobjects

,mydocmode

