drop procedure if exists vk_interest_save
go

create procedure vk_interest_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_interestid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* vkid *//* vkid */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_interest where vk_interestid=g2b(avk_interestid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdinter.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_interest' result;
    leave body;
  end if;
 start transaction ; 
 call vk_interest_logger(acursession,avk_interestid) ; 
 update  vk_interest set changestamp=now()
,
  title=atitle
,
  vkid=avkid
  where  vk_interestid = g2b(avk_interestid) ;


call vk_interest_client_trigger(acursession,avk_interestid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdinter.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_interest' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_interest
 (  vk_interestid 
,instanceid
,title

,vkid

 ) values ( g2b(avk_interestid) 
,g2b(ainstanceid)
,atitle

,avkid

 ) ;


call vk_interest_client_trigger(acursession,avk_interestid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_interest_copy
go

create procedure vk_interest_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_interest 
 ( vk_interestid 
,instanceid
,title

,vkid

 ) select 
copymap(acopyaction,b2g(vk_interestid)) 
,copymap(acopyaction,b2g(instanceid))
,title

,vkid

 from vk_interest
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


