drop procedure if exists vk_camovstat_brief
go

create procedure vk_camovstat_brief  (
 acursession varchar(38),
 avk_camovstatid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_camovstatid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_camovstat where vk_camovstatid=g2b(avk_camovstatid);
 if  aec >0 then
   set abrief=vk_camovstat_brief_f(g2b(avk_camovstatid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_camovstat_delete
go

create procedure vk_camovstat_delete /**/ (
 acursession varchar(38),
 avk_camovstatid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_camovstat where vk_camovstatid=g2b(avk_camovstatid);
if aec>0 then
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_camovstat' result;
    leave body;
  end if;
 call vk_camovstat_logger(acursession,avk_camovstatid) ; 
  delete from  vk_camovstat 
  where  vk_camovstatid = g2b(avk_camovstatid) ;
  delete from num_values where ownerpartname='vk_camovstat' and ownerrowid=g2b(avk_camovstatid);
  end if;
    select 'ok' result;
 end 
go
GO

/*итоговая статистика*/

drop procedure if exists vk_camovstat_save
go

create procedure vk_camovstat_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_camovstatid varchar(38)
,ajoin_rate
 integer/* подписки *//* подписки */
,aspent
 double/* потрачено *//* потрачено */
,aclicks
 integer/* клики *//* клики */
,aimpressions
 integer/* просмотры *//* просмотры */
,avideo_views
 integer/* просмотры *//* просмотры */
,avideo_clicks_site
 integer/* переходы на сайт *//* переходы на сайт */
,avideo_views_half
 integer/* просмотры половины *//* просмотры половины */
,avideo_views_full
 integer/* просмотры целого *//* просмотры целого */
,areach
 integer/* охват *//* охват */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_camovstat where vk_camovstatid=g2b(avk_camovstatid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_camovstat' result;
    leave body;
  end if;
 start transaction ; 
 call vk_camovstat_logger(acursession,avk_camovstatid) ; 
 update  vk_camovstat set changestamp=now()
,
  join_rate=ajoin_rate
,
  spent=aspent
,
  clicks=aclicks
,
  impressions=aimpressions
,
  video_views=avideo_views
,
  video_clicks_site=avideo_clicks_site
,
  video_views_half=avideo_views_half
,
  video_views_full=avideo_views_full
,
  reach=areach
  where  vk_camovstatid = g2b(avk_camovstatid) ;


call vk_camovstat_client_trigger(acursession,avk_camovstatid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_camovstat' result;
    leave body;
  end if;
select count(*) into aec from vk_camovstat where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_camovstat>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_camovstat
 (  vk_camovstatid 
,instanceid
,join_rate

,spent

,clicks

,impressions

,video_views

,video_clicks_site

,video_views_half

,video_views_full

,reach

 ) values ( g2b(avk_camovstatid) 
,g2b(ainstanceid)
,ajoin_rate

,aspent

,aclicks

,aimpressions

,avideo_views

,avideo_clicks_site

,avideo_views_half

,avideo_views_full

,areach

 ) ;


call vk_camovstat_client_trigger(acursession,avk_camovstatid);

 call vkcam_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


