drop procedure if exists vk_camowner_logger
go

create procedure vk_camowner_logger /**/ (
 acursession varchar(38),
 avk_camownerid varchar(38)
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
    leave body;
 end if;
 end 
go
GO


drop procedure if exists vk_camowner_parent
go
create procedure vk_camowner_parent /* */ (
 acursession varchar(38),
 arowid binary(16) ,
out  aparentid binary(16),
out aparenttable varchar(255)
) body:begin  
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
  set aparenttable = 'instance';
  select instanceid into aparentid from vk_camowner where  vk_camownerid=arowid;
 end 
go
GO


drop procedure if exists vk_camowner_islocked
go
create procedure vk_camowner_islocked /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 out aislocked integer
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare alockuserid binary(16);
 declare alocksessionid binary(16);
 declare aparenttable varchar(255); 
declare aec  int;
 set aislocked = 0;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select  usersid into auserid  from the_session where the_sessionid=acursession;
 select  lockuserid,locksessionid into alockuserid, alocksessionid from vk_camowner where vk_camownerid=arowid;
 /* verify this row */
 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists vk_camowner_lock
go
create procedure vk_camowner_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call vk_camowner_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update vk_camowner set lockuserid =auserid ,locksessionid=null  where vk_camownerid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_camowner set lockuserid=null ,locksessionid =acursession  where vk_camownerid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists vk_camowner_unlock
go
create procedure vk_camowner_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_camowner_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_camowner set lockuserid =null  where vk_camownerid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_camowner set locksessionid =null  where vk_camownerid=arowid;
    leave body;
   end if;
 end 
go
GO


drop procedure IF EXISTS vk_camowner_client_trigger
GO

create procedure vk_camowner_client_trigger  (
 acursession varchar(38),
 aVK_CAMOWNERid varchar(38)
) 
 begin  
   declare aBRIEF varchar(255);
  set aBRIEF='to do';
end 
GO


drop procedure if exists vk_camp_brief
go

create procedure vk_camp_brief  (
 acursession varchar(38),
 avk_campid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_campid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_camp where vk_campid=g2b(avk_campid);
 if  aec >0 then
   set abrief=vk_camp_brief_f(g2b(avk_campid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_camp_delete
go

create procedure vk_camp_delete /*рекламная компания*/ (
 acursession varchar(38),
 avk_campid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_camp where vk_campid=g2b(avk_campid);
if aec>0 then
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_camp' result;
    leave body;
  end if;
 call vk_camp_logger(acursession,avk_campid) ; 
  delete from  vk_camp 
  where  vk_campid = g2b(avk_campid) ;
  delete from num_values where ownerpartname='vk_camp' and ownerrowid=g2b(avk_campid);
  end if;
    select 'ok' result;
 end 
go
GO

/*рекламная компания*/

drop procedure if exists vk_camp_save
go

create procedure vk_camp_save /*рекламная компания*/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_campid varchar(38)
,avk_usr varchar(38)/* пользователь *//* пользователь */
,avk_cab varchar(38)/* кабинет *//* кабинет */
,aname
 varchar (255)/* название *//* название */
,acampagin_id
 varchar (30)/* id *//* id */
,astatus varchar(38)/* статус *//* статус */
,aall_limit
 double/* общий лимит *//* общий лимит */
,aday_limit
 double/* дневной лимит *//* дневной лимит */
,astop_time
 datetime/* завершение *//* завершение */
,astart_time
 datetime/* начало *//* начало */
,aprj varchar(38)/* проект *//* проект */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_camp where vk_campid=g2b(avk_campid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_camp' result;
    leave body;
  end if;
 start transaction ; 
 call vk_camp_logger(acursession,avk_campid) ; 
 update  vk_camp set changestamp=now()
,
  vk_usr=g2b(avk_usr)
,
  vk_cab=g2b(avk_cab)
,
  name=aname
,
  campagin_id=acampagin_id
,
  status=g2b(astatus)
,
  all_limit=aall_limit
,
  day_limit=aday_limit
,
  stop_time=astop_time
,
  start_time=astart_time
,
  prj=g2b(aprj)
  where  vk_campid = g2b(avk_campid) ;


call vk_camp_client_trigger(acursession,avk_campid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_camp' result;
    leave body;
  end if;
select count(*) into aec from vk_camp where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_camp>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_camp
 (  vk_campid 
,instanceid
,vk_usr

,vk_cab

,name

,campagin_id

,status

,all_limit

,day_limit

,stop_time

,start_time

,prj

 ) values ( g2b(avk_campid) 
,g2b(ainstanceid)
,g2b(avk_usr)

,g2b(avk_cab)

,aname

,acampagin_id

,g2b(astatus)

,aall_limit

,aday_limit

,astop_time

,astart_time

,g2b(aprj)

 ) ;


call vk_camp_client_trigger(acursession,avk_campid);

 call vkcam_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_camp_copy
go

create procedure vk_camp_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_camp 
 ( vk_campid 
,instanceid
,vk_usr

,vk_cab

,name

,campagin_id

,status

,all_limit

,day_limit

,stop_time

,start_time

,prj

 ) select 
copymap(acopyaction,b2g(vk_campid)) 
,copymap(acopyaction,b2g(instanceid))
,vk_usr

,vk_cab

,name

,campagin_id

,status

,all_limit

,day_limit

,stop_time

,start_time

,prj

 from vk_camp
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


