create procedure vk_camstat_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_camstatid varchar(38)
,ajoin_rate
 integer/* подписки *//* подписки */
,aoveral
 integer/* общая *//* общая */
,as_day
 varchar (10)/* день *//* день */
,aspent
 double/* потрачено *//* потрачено */
,avideo_views
 integer/* просмотры *//* просмотры */
,aimpressions
 integer/* просмотры *//* просмотры */
,avideo_views_full
 integer/* просмотры целого *//* просмотры целого */
,areach
 integer/* охват *//* охват */
,as_month
 varchar (7)/* месяц *//* месяц */
,avideo_views_half
 integer/* просмотры половины *//* просмотры половины */
,aperiod
 varchar (10)/* период *//* период */
,aclicks
 integer/* клики *//* клики */
,avideo_clicks_site
 integer/* переходы на сайт *//* переходы на сайт */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_camstat where vk_camstatid=g2b(avk_camstatid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_camstat' result;
    leave body;
  end if;
 start transaction ; 
 call vk_camstat_logger(acursession,avk_camstatid) ; 
 update  vk_camstat set changestamp=now()
,
  join_rate=ajoin_rate
,
  overal=aoveral
,
  s_day=as_day
,
  spent=aspent
,
  video_views=avideo_views
,
  impressions=aimpressions
,
  video_views_full=avideo_views_full
,
  reach=areach
,
  s_month=as_month
,
  video_views_half=avideo_views_half
,
  period=aperiod
,
  clicks=aclicks
,
  video_clicks_site=avideo_clicks_site
  where  vk_camstatid = g2b(avk_camstatid) ;


call vk_camstat_client_trigger(acursession,avk_camstatid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkcam.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_camstat' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_camstat
 (  vk_camstatid 
,instanceid
,join_rate

,overal

,s_day

,spent

,video_views

,impressions

,video_views_full

,reach

,s_month

,video_views_half

,period

,clicks

,video_clicks_site

 ) values ( g2b(avk_camstatid) 
,g2b(ainstanceid)
,ajoin_rate

,aoveral

