 aVK_CAMSTATid varchar(38)
) 
 begin  
   declare aBRIEF varchar(255);
  set aBRIEF='to do';
end 
GO


drop procedure if exists vk_trgfiles_brief
go

create procedure vk_trgfiles_brief  (
 acursession varchar(38),
 avk_trgfilesid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_trgfilesid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_trgfiles where vk_trgfilesid=g2b(avk_trgfilesid);
 if  aec >0 then
   set abrief=vk_trgfiles_brief_f(g2b(avk_trgfilesid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_trgfiles_delete
go

create procedure vk_trgfiles_delete /**/ (
 acursession varchar(38),
 avk_trgfilesid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_trgfiles where vk_trgfilesid=g2b(avk_trgfilesid);
if aec>0 then
 select checkoperation( acursession ,'vktrg.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_trgfiles' result;
    leave body;
  end if;
 call vk_trgfiles_logger(acursession,avk_trgfilesid) ; 
  delete from  vk_trgfiles 
  where  vk_trgfilesid = g2b(avk_trgfilesid) ;
  delete from num_values where ownerpartname='vk_trgfiles' and ownerrowid=g2b(avk_trgfilesid);
  end if;
    select 'ok' result;
 end 
go
GO

/*списки */

drop procedure if exists vk_trgfiles_save
go

create procedure vk_trgfiles_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_trgfilesid varchar(38)
,aname
 varchar (255)/* название *//* название */
,athefile varchar(255)/* файл */
,athefile_ext varchar(4) /* файл */
,alisttext
 text/* текст списка *//* текст списка */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_trgfiles where vk_trgfilesid=g2b(avk_trgfilesid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vktrg.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_trgfiles' result;
    leave body;
  end if;
 start transaction ; 
 call vk_trgfiles_logger(acursession,avk_trgfilesid) ; 
 update  vk_trgfiles set changestamp=now()
,
  name=aname
,
  thefile=athefile
,thefile_ext=
athefile_ext 
,
  listtext=alisttext
  where  vk_trgfilesid = g2b(avk_trgfilesid) ;


call vk_trgfiles_client_trigger(acursession,avk_trgfilesid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vktrg.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_trgfiles' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_trgfiles
 (  vk_trgfilesid 
,instanceid
,name

,thefile

,thefile_ext
,listtext

 ) values ( g2b(avk_trgfilesid) 
,g2b(ainstanceid)
,aname

,athefile

,athefile_ext
,alisttext

 ) ;


call vk_trgfiles_client_trigger(acursession,avk_trgfilesid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists vk_trgfiles_copy
go

create procedure vk_trgfiles_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into vk_trgfiles 
 ( vk_trgfilesid 
,instanceid
,name

,thefile

,thefile_ext
,listtext

 ) select 
copymap(acopyaction,b2g(vk_trgfilesid)) 
,copymap(acopyaction,b2g(instanceid))
,name

,thefile

,thefile_ext
,listtext

 from vk_trgfiles
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


