drop procedure if exists vk_trginfo_save
go

create procedure vk_trginfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_trginfoid varchar(38)
,aname
 varchar (255)/* название *//* название */
,acab varchar(38)/* кабинет *//* кабинет */
,avkid
 varchar (30)/* id *//* id */
,ainfo
 varchar (4096)/* описание *//* описание */
,atrgdomain
 varchar (255)/* домен *//* домен */
,aaudience_count
 integer/* аудитория *//* аудитория */
,alifetime
 integer/* автоудаление через (дн.) *//* автоудаление через (дн.) */
,apixel
 varchar (4096)/* код для вставки *//* код для вставки */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_trginfo where vk_trginfoid=g2b(avk_trginfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vktrg.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_trginfo' result;
    leave body;
  end if;
 start transaction ; 
 call vk_trginfo_logger(acursession,avk_trginfoid) ; 
 update  vk_trginfo set changestamp=now()
,
  name=aname
,
  cab=g2b(acab)
,
  vkid=avkid
,
  info=ainfo
,
  trgdomain=atrgdomain
,
  audience_count=aaudience_count
,
  lifetime=alifetime
,
  pixel=apixel
  where  vk_trginfoid = g2b(avk_trginfoid) ;


call vk_trginfo_client_trigger(acursession,avk_trginfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vktrg.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_trginfo' result;
    leave body;
  end if;
select count(*) into aec from vk_trginfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_trginfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_trginfo
 (  vk_trginfoid 
,instanceid
,name

,cab

,vkid

,info

,trgdomain

,audience_count

,lifetime

,pixel

 ) values ( g2b(avk_trginfoid) 
,g2b(ainstanceid)
,aname

,g2b(acab)

,avkid

,ainfo

,atrgdomain

,aaudience_count

,alifetime

,apixel

 ) ;


call vk_trginfo_client_trigger(acursession,avk_trginfoid);

 call vktrg_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


