 go
GO


drop procedure if exists vk_trginfo_lock
go
create procedure vk_trginfo_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call vk_trginfo_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update vk_trginfo set lockuserid =auserid ,locksessionid=null  where vk_trginfoid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_trginfo set lockuserid=null ,locksessionid =acursession  where vk_trginfoid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists vk_trginfo_unlock
go
create procedure vk_trginfo_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_trginfo_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_trginfo set lockuserid =null  where vk_trginfoid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_trginfo set locksessionid =null  where vk_trginfoid=arowid;
    leave body;
   end if;
 end 
go
GO


