    leave body;
  end if;
   if  alockmode =2  then
    update vk_trgowner set lockuserid =auserid ,locksessionid=null  where vk_trgownerid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update vk_trgowner set lockuserid=null ,locksessionid =acursession  where vk_trgownerid=arowid;
     leave body;
   end if;
 end 
 go 
GO


