drop procedure if exists vk_brkinfo_save
go

create procedure vk_brkinfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_brkinfoid varchar(38)
,acrdate
 datetime/* дата создания *//* дата создания */
,aads varchar(38)/* объявление *//* объявление */
,arotation varchar(38)/* ротация *//* ротация */
,aname
 varchar (255)/* название *//* название */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_brkinfo where vk_brkinfoid=g2b(avk_brkinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkbrk.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_brkinfo' result;
    leave body;
  end if;
 start transaction ; 
 call vk_brkinfo_logger(acursession,avk_brkinfoid) ; 
 update  vk_brkinfo set changestamp=now()
,
  crdate=acrdate
,
  ads=g2b(aads)
,
  rotation=g2b(arotation)
,
  name=aname
  where  vk_brkinfoid = g2b(avk_brkinfoid) ;


call vk_brkinfo_client_trigger(acursession,avk_brkinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkbrk.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_brkinfo' result;
    leave body;
  end if;
select count(*) into aec from vk_brkinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <vk_brkinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   vk_brkinfo
 (  vk_brkinfoid 
,instanceid
,crdate

,ads

,rotation

,name

 ) values ( g2b(avk_brkinfoid) 
,g2b(ainstanceid)
,acrdate

,g2b(aads)

,g2b(arotation)

,aname

 ) ;


call vk_brkinfo_client_trigger(acursession,avk_brkinfoid);

 call vkbrk_initownership( acursession,ainstanceid);
 end if;
 commit; 
select 'ok' result;
 end 
go
GO


