 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_brkownerid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_brkowner where vk_brkownerid=g2b(avk_brkownerid);
 if  aec >0 then
   set abrief=vk_brkowner_brief_f(g2b(avk_brkownerid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_brkowner_delete
go

create procedure vk_brkowner_delete /**/ (
 acursession varchar(38),
 avk_brkownerid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_brkowner where vk_brkownerid=g2b(avk_brkownerid);
if aec>0 then
 select checkoperation( acursession ,'vkbrk.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_brkowner' result;
    leave body;
  end if;
 call vk_brkowner_logger(acursession,avk_brkownerid) ; 
  delete from  vk_brkowner 
  where  vk_brkownerid = g2b(avk_brkownerid) ;
  delete from num_values where ownerpartname='vk_brkowner' and ownerrowid=g2b(avk_brkownerid);
  end if;
    select 'ok' result;
 end 
go
GO

/*владелец*/

drop procedure if exists vk_brkowner_save
go

create procedure vk_brkowner_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_brkownerid varchar(38)
,avkusr varchar(38)/* владелец *//* владелец */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_brkowner where vk_brkownerid=g2b(avk_brkownerid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vkbrk.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_brkowner' result;
    leave body;
  end if;
 start transaction ; 
 call vk_brkowner_logger(acursession,avk_brkownerid) ; 
 update  vk_brkowner set changestamp=now()
,
  vkusr=g2b(avkusr)
  where  vk_brkownerid = g2b(avk_brkownerid) ;


call vk_brkowner_client_trigger(acursession,avk_brkownerid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vkbrk.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_brkowner' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_brkowner
 (  vk_brkownerid 
,instanceid
,vkusr

 ) values ( g2b(avk_brkownerid) 
,g2b(ainstanceid)
,g2b(avkusr)

 ) ;


call vk_brkowner_client_trigger(acursession,avk_brkownerid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


