 select checkoperation( acursession ,'mtzfltr.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=filterfieldgroup' result;
    leave body;
  end if;
 start transaction;  
 insert into   filterfieldgroup
 (  filterfieldgroupid 
,instanceid
,sequence

,name

,caption

,allowignore

 ) values ( g2b(afilterfieldgroupid) 
,g2b(ainstanceid)
,asequence

,aname

,acaption

,aallowignore

 ) ;


