drop procedure if exists vk_castate_brief
go

create procedure vk_castate_brief  (
 acursession varchar(38),
 avk_castateid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if avk_castateid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from vk_castate where vk_castateid=g2b(avk_castateid);
 if  aec >0 then
   set abrief=vk_castate_brief_f(g2b(avk_castateid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists vk_castate_delete
go

create procedure vk_castate_delete /**/ (
 acursession varchar(38),
 avk_castateid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_castate where vk_castateid=g2b(avk_castateid);
if aec>0 then
 select checkoperation( acursession ,'vdcst.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_castate' result;
    leave body;
  end if;
 call vk_castate_logger(acursession,avk_castateid) ; 
  delete from  vk_castate 
  where  vk_castateid = g2b(avk_castateid) ;
  delete from num_values where ownerpartname='vk_castate' and ownerrowid=g2b(avk_castateid);
  end if;
    select 'ok' result;
 end 
go
GO

/*статус*/

drop procedure if exists vk_castate_save
go

create procedure vk_castate_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_castateid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* id *//* id */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_castate where vk_castateid=g2b(avk_castateid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdcst.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_castate' result;
    leave body;
  end if;
 start transaction ; 
 call vk_castate_logger(acursession,avk_castateid) ; 
 update  vk_castate set changestamp=now()
,
  title=atitle
,
  vkid=avkid
  where  vk_castateid = g2b(avk_castateid) ;


call vk_castate_client_trigger(acursession,avk_castateid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdcst.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_castate' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_castate
 (  vk_castateid 
,instanceid
,title

,vkid

 ) values ( g2b(avk_castateid) 
,g2b(ainstanceid)
,atitle

,avkid

 ) ;


call vk_castate_client_trigger(acursession,avk_castateid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


