drop procedure if exists vk_category_delete
go

create procedure vk_category_delete /**/ (
 acursession varchar(38),
 avk_categoryid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_category where vk_categoryid=g2b(avk_categoryid);
if aec>0 then
 select checkoperation( acursession ,'vdcat.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=vk_category' result;
    leave body;
  end if;
 call vk_category_logger(acursession,avk_categoryid) ; 
  delete from  vk_category 
  where  vk_categoryid = g2b(avk_categoryid) ;
  delete from num_values where ownerpartname='vk_category' and ownerrowid=g2b(avk_categoryid);
  end if;
    select 'ok' result;
 end 
go
GO

