drop procedure if exists vk_category_save
go

create procedure vk_category_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 avk_categoryid varchar(38)
,atitle
 varchar (255)/* название *//* название */
,avkid
 integer/* id *//* id */
,aparent_id
 integer/* родительская категория *//* родительская категория */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare asessuserid binary(16);
 declare amlf_partid binary(16);
 declare asessuserlogin varchar(40);
 declare aec int;
 select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
 select login into asessuserlogin from users where usersid=g2b(asessuserid);
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from vk_category where vk_categoryid=g2b(avk_categoryid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'vdcat.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=vk_category' result;
    leave body;
  end if;
 start transaction ; 
 call vk_category_logger(acursession,avk_categoryid) ; 
 update  vk_category set changestamp=now()
,
  title=atitle
,
  vkid=avkid
,
  parent_id=aparent_id
  where  vk_categoryid = g2b(avk_categoryid) ;


call vk_category_client_trigger(acursession,avk_categoryid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'vdcat.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=vk_category' result;
    leave body;
  end if;
 start transaction;  
 insert into   vk_category
 (  vk_categoryid 
,instanceid
,title

,vkid

,parent_id

 ) values ( g2b(avk_categoryid) 
,g2b(ainstanceid)
,atitle

,avkid

,aparent_id

 ) ;


call vk_category_client_trigger(acursession,avk_categoryid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


