    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call vk_category_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update vk_category set lockuserid =null  where vk_categoryid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update vk_category set locksessionid =null  where vk_categoryid=arowid;
    leave body;
   end if;
 end 
go
GO


